import React, { useEffect, useState } from 'react' 

function Reminder({reminder}) {
    const [timeRemaining, setTimeRemaining] = useState({})
    var days, hrs, mins, secs

    const findTimeLeft = () => {
        const remindDate = reminder.date
        const present = new Date()
        const secDiff = (remindDate.getTime() - present.getTime()) / 1000
        let timeLeft = {}

        if (secDiff > 0) {
            timeLeft = {
                days: Math.floor(secDiff / (60 * 60 * 24)),
                hours: Math.floor((secDiff / (60 * 60)) % 24),
                minutes: Math.floor((secDiff / 60) % 60),
                seconds: Math.floor(secDiff % 60)
            }
            setTimeRemaining(timeLeft)
        }
    }

    useEffect(() => {
        setInterval(() => findTimeLeft(), 1000)
    }, [])

    if (timeRemaining.days > 0) {
        days = `${timeRemaining.days} D`
    }
    if (timeRemaining.hours && timeRemaining.hours > 0) {
        hrs = `${timeRemaining.hours} H`
    }
    // if (timeRemaining.days && timeRemaining.days > 0) {
    //     days = `${timeRemaining.days} D`
    // }
    // if (timeRemaining.days && timeRemaining.days > 0) {
    //     days = `${timeRemaining.days} D`
    //}

    return (
        <div className="reminder">
            <h2>{reminder.text}</h2>
            <p>Time until Reminded: 
                <span style={{fontWeight: "bold"}}>
                    {days} 
                    {timeRemaining.hours} H 
                    {timeRemaining.minutes} M 
                    {timeRemaining.seconds} S 
                </span>
            </p>
        </div>
    )
}

export default Reminder
